import {useContext, useState} from 'react';

const FullscreenPropsContext = createContext();

export function useFullscreenPropsContext(){
    return useContext(FullscreenPropsContext)
};

export function PartyRoomPropsProvider({children}){

    const [showChat, setShowChat] = useState(true);
    const partyRoomProps = {showChat, setShowChat};

    return (
        <PartyRoomPropsContext.Provider value={{partyRoomProps}}>
            {children}
        </PartyRoomPropsContext.Provider>
    );
}
