import { Message, PeopleTwoTone } from '@mui/icons-material';
import { Box } from '@mui/material';
import React, {useState} from 'react';
import './TabSwitch.scss'

const TabSwitch = () => {

    const [showChat, setShowChat] = useState(true);

    return(
        <Box id='tab-switch' onClick={()=> setShowChat(!showChat)}>
            <Message className={`icon ${ showChat ? 'active-tab' : ''}`}/>
            <PeopleTwoTone className={`icon ${ !showChat ? 'active-tab' : ''}`}/>
        </Box>
    )

};

export default TabSwitch;
