import React, {useState} from 'react';
import {Box, Modal} from '@mui/material';
import {CTAButton} from '../../components';
import './ModalPopup.scss';

const ModalPopup = ({open, handleClose, text, ctaText, title, usesInput}) => {

  const[inputText, setInputText] = useState('');
  const[isError, setIsError] = useState('true');

  return (
    <Modal
        open={open}
        onClose={handleClose}
    >
        <Box className="modal-inner" >
            <h2>{title}</h2>
            {text && <p>{text}</p>}
            { 
              usesInput && 
                <input 
                  className="text-value-input"
                  onChange={(e)=> setInputText(e.target.value)}
                />
            }

            { 
              usesInput && isError &&
                <span className="error-message">{isError}</span>
            }
            <CTAButton 
              text={ctaText} 
              buttonFunction={()=>{
                handleClose();
                if(!usesInput) navigator.clipboard.writeText(text)
              }}
            />
        </Box>  
    </Modal>
  )
}

export default ModalPopup;
