import React from 'react';
import {Box, Modal} from '@mui/material';
import {CTAButton} from '../../components';

// const style = {
//     position: 'absolute',
//     top: '50%',
//     left: '50%',
//     transform: 'translate(-50%, -50%)',
//     width: '90%',
//     maxWidth: 400,
//     bgcolor: '#141414',
//     border: '1px solid #202020',
//     boxShadow: 24,
//     p: 4,
//     display: 'flex',
//     flexDirection: 'column',
//     justifyContent: 'space-between',
//     alignItems: 'center',
//     textAlign: 'center',
//     gap: '10px'
// };

const style = {
    position:'absolute',
    top: '50%',
    left: '50%',
    transform:"translate(-50%, -50%)",
    bgcolor: '#272727',
    mwidth: '90%'

};

const titleStyles = {
    fontStyles: '"Calibre", "Inter", "San Francisco", "SF Pro Text", -apple-system, system-ui, sans-serif'
};

const ModalPopup = ({open, handleClose, text, ctaText, title}) => {
  return (
    <Modal
        open={open}
        onClose={handleClose}
    >
        <Box className="modal-inner" sx={style}>
            <h2 sx={titleStyles}>{title}</h2>
            <p>{text}</p>
            <CTAButton text={ctaText} buttonFunction={handleClose}/>
        </Box>  
    </Modal>
  )
}

export default ModalPopup;
